package test;

public class Page36 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = +1;
		int n2 = -2;
		int n3 = 5;
		
		n3++;
		System.out.println(n3);
		System.out.println(n3++);
		System.out.println(++n3);
		
		--n3;
		System.out.println(n3);
		System.out.println(n3--);
		System.out.println(--n3);
		
		boolean bRet = true;
		System.out.println(!bRet);
		
	}

}
