package test;

public class Page37 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int e = 0;
		System.out.println("월: "+ ++e+"개");
		System.out.println("화: "+ ++e+"개");
		System.out.println("수: "+ ++e+"개");
		System.out.println("목: "+ ++e+"개");
		System.out.println("금: "+ ++e+"개");
		System.out.println("토: "+ ++e+"개");
		System.out.println("일: "+ ++e+"개");
		
		int n = 0;
		for (n=0; n<7;n=1+n) 
		{
			System.out.println("알"+n+"개");
		}
		
		
	}

}
